interface ICaviar {
    function mint(address,uint256) external;
    function burn(address,uint256) external;
    function updateSupplyAtCurrentEpoch(uint256) external;
    function totalSupply() external view returns(uint256);
    function supplyAtCurrentEpoch() external view returns(uint256);
}","keccak256":"0x401ed42302f61c68ca83f5279176b653304942aaf53d3c771ed6b6e527d31745","license":"MIT"},"contracts/interfaces/ICaviarChef.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface ICaviarChef {
    function balanceOf() external view returns(uint256);
    function userInfo(address _user) external view returns (uint256, uint256);
    function update(address from, address to) external;
    function harvestRebase(address from, address to) external;
    function underlying() external view returns (IERC20);
    function seedRewards(uint256 _amount) external;
    function claimEmissions() external returns (uint256);
}","keccak256":"0x89728ee4da4f7e4b5205e029f0e28be8429e8df8ab3c93d973a4a7f8dee941ec","license":"MIT"},"contracts/interfaces/ICaviarFeeManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

interface ICaviarFeeManager {
    function distributeFees() external;
    function distributeRebaseFees(uint) external;
